import Vue from 'vue'
import VueRouter from 'vue-router'

import Login from '../components/system/Login.vue'
import Home from '../components/system/Home.vue'

// 1.通过Vue.use(插件),安装插件
Vue.use(VueRouter);

// 2.创建VueRouter对象
const routes = [{
		path: '/',
		component: Login
	},{
		path: '/home',
		component: Home
	}
]
const router = new VueRouter({
	//配置路由和组件之间的应用关系
	routes
})

// 3.将router对象传入到Vue实例
export default router
